(()=>{"use strict";window.distance=function(){let e=document.getElementById("dist").value,t="";switch(document.getElementById("OptionDist").value){case"K":t=(a=e)+"km is "+a/1.609+" miles";break;case"M":t=(n=e)+"m is "+1.609*n+" km"}var n,a;document.getElementById("ResDist").value=t},window.temperature=function(){let e=document.getElementById("temp").value,t="";switch(document.getElementById("Option").value){case"C":t=(s=e)+"°C is "+(9*s/5+32)+"°F and "+(s+273.15)+"°K";break;case"F":t=(a=e)+"°F is "+5*(a-32)/9+"°C and "+(5*(a-32)/9+273.15)+"°K";break;case"K":t=(n=e)+"°K is "+(n-273.15)+"°C and "+(9*(n-273.15)/5+32)+"°F."}var n,a,s;document.getElementById("Res").value=t}})();